import React, { useEffect, useState } from "react";
import axios from "axios";

interface Livro {
  id: number;
  titulo: string;
  autor: string;
  categoria: string;
  status: string;
}

interface Props {
  atualizar: boolean;
}

const ListaLivros: React.FC<Props> = ({ atualizar }) => {
  const [livros, setLivros] = useState<Livro[]>([]);

  useEffect(() => {
    axios.get("http://localhost:8080/livros")
      .then(response => setLivros(response.data))
      .catch(error => console.error("Erro ao buscar livros:", error));
  }, [atualizar]);

  const trocarStatus = async (id: number, statusAtual: string) => {
    const novoStatus = statusAtual === "DISPONIVEL" ? "EMPRESTADO" : "DISPONIVEL";
    try {
      await axios.put(`http://localhost:8080/livros/${id}/status?status=${novoStatus}`);
      // Atualiza lista após alteração
      const resposta = await axios.get("http://localhost:8080/livros");
      setLivros(resposta.data);
    } catch (error) {
      console.error("Erro ao atualizar status:", error);
    }
  };

  return (
    <div>
      <h2>Lista de Livros</h2>
      <ul>
        {livros.map(livro => (
          <li key={livro.id}>
            {livro.titulo} - {livro.autor} ({livro.categoria}) - {livro.status}{" "}
            <button onClick={() => trocarStatus(livro.id, livro.status)}>
              {livro.status === "DISPONIVEL" ? "Emprestar" : "Devolver"}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ListaLivros;
